import React, { useState, useRef, useEffect } from "react";
import ACTIONS from "../Actions";
import Client from "../components/Client";
import Editor from "../components/Editor";
import { initSocket } from "../socket";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-hot-toast";
import axios from "axios";

const EditorPage = () => {
    const socketRef = useRef(null);
    const codeRef = useRef(null);
    const location = useLocation();
    const reactNavigator = useNavigate();
    const { roomId } = useParams();
    const [clients, setClients] = useState([]);

    useEffect(() => {
        const init = async () => {
            socketRef.current = await initSocket();

            socketRef.current.on("connect_error", handleErrors);
            socketRef.current.on("connect_failed", handleErrors);

            function handleErrors(err) {
                console.error("Socket connection error:", err);
                toast.error("Socket connection failed, try again later.");
                reactNavigator("/");
            }

            socketRef.current.emit(ACTIONS.JOIN, {
                roomId,
                username: location.state?.username,
            });

            socketRef.current.on(ACTIONS.JOINED, ({ clients, username, socketId }) => {
                if (username !== location.state?.username) {
                    toast.success(`${username} joined the room.`);
                }
                setClients(clients);
                socketRef.current.emit(ACTIONS.SYNC_CODE, {
                    code: codeRef.current,
                    socketId,
                });
            });

            socketRef.current.on(ACTIONS.DISCONNECTED, ({ socketId, username }) => {
                toast.success(`${username} left the room.`);
                setClients((prev) => prev.filter((client) => client.socketId !== socketId));
            });
        };
        init();

        return () => {
            socketRef.current.off(ACTIONS.JOINED);
            socketRef.current.off(ACTIONS.DISCONNECTED);
            socketRef.current.disconnect();
        };
    }, []);

    const runCode = () => {
        const lang = document.getElementById("languageOptions").value;
        const input = document.getElementById("input").value;
        const code = codeRef.current;

        toast.loading("Running Code....");

        const encodedParams = new URLSearchParams();
        encodedParams.append("LanguageChoice", lang);
        encodedParams.append("Program", code);
        encodedParams.append("Input", input);

        const API_KEY = process.env.REACT_APP_API_KEY;

        if (!API_KEY) {
            console.error("API key is missing! Make sure .env file is set up correctly.");
            toast.error("API Key is missing. Check .env file.");
            return;
        }

        const options = {
            method: "POST",
            url: "https://code-compiler.p.rapidapi.com/v2",
            headers: {
                "content-type": "application/x-www-form-urlencoded",
                "X-RapidAPI-Key": API_KEY,
                "X-RapidAPI-Host": "code-compiler.p.rapidapi.com",
            },
            data: encodedParams,
        };

        axios
            .request(options)
            .then((response) => {
                let message = response.data.Result || response.data.Errors || "No output received.";
                document.getElementById("input").value = message;
                toast.dismiss();
                toast.success("Code executed successfully");
            })
            .catch((error) => {
                console.error("Error:", error);
                toast.dismiss();
                toast.error("Code execution failed");
                document.getElementById("input").value = "Something went wrong. Check your code and input.";
            });
    };

    return (
        <div className="mainWrap">
            <div className="asideWrap">
                <div className="asideInner">
                    <div className="logo">
                        <img className="logoImage" src="/code-sync.png" alt="logo" />
                    </div>
                    <h3>Connected</h3>
                    <div className="clientsList">
                        {clients.map((client) => (
                            <Client key={client.socketId} username={client.username} />
                        ))}
                    </div>
                </div>
                <button className="btn runBtn" onClick={runCode}>Run Code</button>
            </div>

            <div className="editorWrap">
                <Editor
                    socketRef={socketRef}
                    roomId={roomId}
                    onCodeChange={(code) => {
                        codeRef.current = code;
                    }}
                />
                <textarea id="input" className="inputArea" placeholder="Enter your input here"></textarea>
            </div>
        </div>
    );
};

export default EditorPage;
